function genJwtToken(reqBody) {
    var md5Str = new Buffer(reqBody.pwd,'base64').toString();
    var dgst_str=context.global.WinkUser.uid+':'+context.global.WinkUser.pwd;
    var md5dgst=context.global.CRYPTO.createHash("md5").update(dgst_str,"utf8").digest('hex');
    var clientId = new Buffer(reqBody.uuid,'base64').toString();
    node.warn(clientId);
    if (reqBody.uid==context.global.WinkUser.uid && md5Str==md5dgst) {
        var token = context.global.wnr.jwt.sign({'cliendId':clientId},context.global.FREEBOARD_TOKEN);
        node.warn(token);
        flow.set('token',token);
        return true;
    } else return false;
}
