var lights=[];
if ('light_bulbs' in context.global.winkState){
        for (var l_bulb in context.global.winkState.light_bulbs){
            var bulb=context.global.winkState.light_bulbs[l_bulb];
            if ('color_model' in bulb || 'hue' in bulb) {
                var color=context.global.tinycolor('hsv '+(bulb.eco_system=='LIFX' ? bulb.hue : bulb.hue*360)+' '+bulb.saturation+' '+bulb.brightness);
                var hexColor=color.toHexString();
            }            
            lights.push({
            name:bulb.name,
            clr_id:'.clr-'+bulb.object_id,
            isColor: (('color_model' in bulb || 'hue' in bulb) ? true:false),
            Color:(('color_model' in bulb || 'hue' in bulb) ? hexColor : null),
            class_name:'dial '+bulb.object_id,
            object_id:bulb.object_id,
            powered:bulb.powered,
            not_powered:!bulb.powered,
            step:1,
            brightness:Math.round(bulb.brightness*100)
        });
    }
    msg.payload.haveLights=true;
}
if ('binary_switches' in context.global.winkState){
    for (var bswitch in context.global.winkState.binary_switches){
        var bulb=context.global.winkState.binary_switches[bswitch];
        lights.push({
            name:bulb.name,
            class_name:'dial '+bulb.object_id,
            object_id:bulb.object_id,
            powered:bulb.powered,
            step:100,
            brightness:(bulb.powered ? 100:0)
        });
}
    msg.payload.haveLights=true;
}

if ('powerstrips' in context.global.winkState){
    for (var bswitch in context.global.winkState.powerstrips){
        var bulb=context.global.winkState.powerstrips[bswitch];
        if (bulb.object_type==='outlet'){
        lights.push({
            name:bulb.name,
            class_name:'dial '+bulb.object_id,
            object_id:bulb.object_id,
            powered:bulb.powered,
            step:100,
            brightness:(bulb.powered ? 100:0)
        });
    }
}
    msg.payload.haveLights=true;
}
msg.payload.lights=lights;
