if ('bfp' in msg.payload && context.global.getCookieVerified('exchange_token',msg.req.headers.cookie, msg.payload.bfp)==context.global.FREEBOARD_TOKEN){
msg.statusCode="200";
msg.headers={'Cache-Control':"max-age=0,no-cache,no-store,post-check=0,pre-check=0"};
msg.payload.pwd_str=context.global.FREEBOARD_TOKEN;
msg.payload.ifttt_token=context.global.FREEBOARD_TOKEN;
msg.payload.haveRobots=false
var robots=[]
if ('robots' in context.global.winkState){
        for (var l_robot in context.global.winkState.robots){
            var robot=context.global.winkState.robots[l_robot];
            robots.push({
            name:robot.name,
            //class_name:'dial '+bulb.object_id,
            object_id:robot.object_id,
            enabled:robot.enabled,
            not_enabled:!robot.enabled
        });
    }
    msg.payload.haveRobots=true
}
msg.payload.robots=robots;
if (msg.topic=='json'){
    var Etag="";
    if ('CRYPTO' in context.global) Etag=context.global.CRYPTO.createHash("md5").update(JSON.stringify(msg.payload),"utf8").digest("hex");
    msg.headers={
      'Content-Type': 'application/json',
      'ETag': Etag
    };
}
} else {
msg.statusCode="403";
}
return msg;

//if(msg.req.headers.authorization && msg.req.headers.authorization=="Bearer "+context.global.FREEBOARD_TOKEN){
//if ((msg.req.body.uid==context.global.WinkUser.uid && msg.req.body.pwd==context.global.WinkUser.pwd) ||context.global.getCookie('exchange_token',msg.req.headers.cookie)==context.global.FREEBOARD_TOKEN || msg.payload.token==context.global.FREEBOARD_TOKEN){
//if (true){
if ('bfp' in msg.payload && context.global.getCookieVerified('exchange_token',msg.req.headers.cookie, msg.payload.bfp)==context.global.FREEBOARD_TOKEN){    
msg.statusCode="200";
msg.payload.pwd_str=context.global.FREEBOARD_TOKEN;
msg.payload.ifttt_token=context.global.FREEBOARD_TOKEN;
cameras=[]
for (var cam in context.global.winkState.cameras){
    var camera=context.global.winkState.cameras[cam];
    if(camera.device_manufacturer!='canary'){
    cameras.push({
        name:camera.name,
        id:camera.object_id,
        connection:camera.connection,
        url:camera.snap_url,
        history_url:camera.history_url,
        refresh_interval:30
    });
   }
}
msg.payload.cameras=cameras;
msg.res.statusCode="200";
} else {
msg.statusCode="403";
}
return msg;
